    .top-search-form .s-ipt-wr:hover,
    .wgt-comments .cmt-btn-w .cmt-btn:hover {
        box-shadow: 0 2px 5px #0000003b
    }
    .bottom-search-form #bsb,
    .top-search-form #sb,
    .top-search-form .s-btn {
        color: #fff;
        transition: .3s;
        border-radius: 0 5px 5px 0;
        font-size: 10px;
        background: #5892e3;
        border: none;
        box-shadow: 0 2px 5px #0000001f
    }
    .bottom-search-form #bsb:hover,
    .top-search-form #sb:hover,
    .top-search-form .s-btn:hover {
        background: #4879bd;
        box-shadow: 0 2px 5px #0000003b
    }
    #ihome-header .search-box-main .search-form #search-btn {
        width: 70px;
        height: 32px;
        left: 321px;
        top: 0
    }
    .search-box .inner-warp {
        height: 90px
    }
    .search-box a.logo {
        left: 0;
        bottom: auto;
        top: 30%
    }
    .userbar .dropdown-menu a {
        border-bottom: none
    }
    .userbar .menu-hide .dropdown-menu {
        transition: .3s;
        display: block;
        opacity: 0
    }
    .userbar .menu-show .dropdown-menu {
        transition: .3s;
        opacity: 1
    }
    .userbar .dropdown-menu {
        top: 20px;
        border: none;
        box-shadow: 0 2px 5px #0000005e
    }
    .top-search-box {
        overflow: visible;
        margin-bottom: 20px
    }
    .top-search-form .s-ipt-wr {
        width: 508px
    }
    .l-main-col {
        margin-left: 30%
    }
    .wgt-bread {
        background: #fff0;
        color: #000;
        height: 24px;
        line-height: 24px;
        margin: 0 35% 0 22%;
        padding: 0;
        position: relative
    }
    .s-nav-tab {
        margin: 0;
        padding: 0;
        background: #fff0;
        height: 33px;
        margin-bottom: 21px
    }
    .search-list dl a:link,
    .wgt-relate-search a:link,
    .wgt-relate-search a:visited {
        color: #4879bd
    }
    .search-list dl {
        float: left;
        width: 100%;
        padding: 20px 20px 15px;
        margin-top: 0;
        margin-bottom: 20px;
        border-radius: 0;
        background-color: #fff;
        box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
        -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
        -moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
        transition: all .25s cubic-bezier(.23, 1, .32, 1) 0s
    }
    .search-list dl:hover {
        box-shadow: 0 0 2px gray !important;
        background: #f3f3f3 !important
    }
    .pg b {
        color: #fff;
        background: #2aa198;
        border: 1px solid #fff0
    }
    .pg a {
        border: 1px solid #2aa19845;
        color: #2aa198;
        width: 25px
    }
    .search-list dt {
        margin-bottom: 10px
    }
    .main-content-bottom {
        border: none;
        border-top: 1px solid rgba(0, 0, 0, .04)
    }
    .top-search-box ul.channel {
        padding: 19px 0 10px 0
    }
    .list-icon {
        border-radius: 50%;
        background: #2aa198
    }
    .enter-step-btn {
        transition: .3s
    }
    .exp-image-wraper .enter-step-btn {
        display: block;
        opacity: 0
    }
    .exp-image-wraper:hover .enter-step-btn {
        transition: .3s;
        opacity: 1
    }
    .list-icon-link {
        transition: .1s;
        background: url(data:image/png;base64,) no-repeat 0 -68px
    }
    .list-icon-current {
        background-position: 0 0
    }
    .exp-content ul li {
        background: url(data:image/png;base64,) no-repeat scroll 0 8px transparent
    }
    .wgt-exp-share .wrp-exp-share a {
        border-radius: 50%;
        margin-bottom: 10px;
        box-shadow: 0 3px 7px rgba(0, 0, 0, .18)
    }
    #bdshare.exp-share-wgt {
        border-radius: 100px;
        overflow: hidden;
        box-shadow: 0 3px 7px rgba(0, 0, 0, .18)
    }
    .exp-item-list .related-exp-link {
        transition: .3s;
        padding: 0 20px;
        border-bottom: none
    }
    .exp-item-list .related-exp-link:nth-child(2n+1) {
        background-color: #00000008
    }
    .exp-item-list .related-exp-link:hover {
        background-color: #00000012
    }
    .exp-date {
        right: 20px
    }
    .main-content-bottom {
        margin-bottom: 30px
    }
    .wgt-img-exp .exp-info {
        background: #fff0;
        border: none
    }
