        position: absolute;
        width: 69px;
        height: 70px;
        background: #fff0
    }
    .base-info .author-portrait img {
        border-radius: 50%;
        width: 50px;
        height: 50px
    }
    .top-nav-bar {
        display: none;
        height: 44px;
        width: 100%;
        position: fixed;
        top: 0;
        left: 0;
        z-index: 1010;
        background: #ffffffdb;
        box-shadow: 0 5px 10px #0000002b
    }
    .top-nav-bar .topsearch .toptextinput {
        transition: .3s;
        border: none;
        border-bottom: 2px solid #00000030
    }
    .top-nav-bar .topsearch .toptextinput:focus {
        border: none;
        border-bottom: 2px solid #2aa198
    }
    .body-wrapper .content-container {
        animation-name: left_logoR;
        -webkit-animation-duration: .2s;
        -webkit-animation-timing-function: ease
    }
    #bread-wrap,
    .body-wrapper,
    .breadcrumb {
        background-color: #f1f1f1
    }
    .body-wrapper .content-container .main-content {
        background-color: #fefefe
    }
    .top-search-box .s-nav {
        height: 20px
    }
    .userbar {
        top: 30px
    }
    .breadcrumb .bread-wrap,
    .content-container,
    .header,
    .nav .nav-wrap {
        width: 1030px;
        margin: 0 auto
    }
    #main-content {
        padding: 0;
        width: inherit;
        margin: 10px auto;
        border-radius: 3px;
        box-shadow: 0 1px 10px rgba(0, 0, 0, .12);
        -webkit-box-shadow: 0 1px 10px rgba(0, 0, 0, .12);
        -moz-box-shadow: 0 1px 10px rgba(0, 0, 0, .12)
    }
    #exp-article > div[class^=exp-],
    #exp-article > div[class^=wgt-],
    #wgt-comments,
    .main-content-bottom {
        padding: 10px 50px
    }
    .wgt-common-nav .share-exp-e {
        width: 192px;
        margin-right: 20px
    }
    .wgt-thumbs {
        background-size: auto 70px
    }
    #wgt-exp-share {
        bottom: 100px !important
    }
    .wgt-hot-exp .exp-item {
        transition: .3s;
        box-shadow: 0 1px 5px rgba(0, 0, 0, .08)
    }
    .wgt-hot-exp .exp-item:hover {
        box-shadow: 0 5px 10px rgba(0, 0, 0, .18)
    }
