@-moz-document domain("xueshu.baidu.com") {
  .rs_banner {
    display: none !important
  }
  #navcard_wrapper .navlist_content .navitem, #platform_menu .menuitem, .sc_default_result .sc_other a {
    transition: .3s
  }
  #dtl_l .c_content a:hover, #dtl_l .relative_content a:hover, #dtl_l h3 a:hover, a, a:hover {
    text-decoration: none !important
  }
  #fm .s_ipt_wr, #leftnav #kw {
    transition: .3s;
    background: #fff;
    padding: 1px;
    border: none !important;
    border-radius: 5px 0 0 5px;
    box-shadow: 0 2px 5px #0000001f
  }
  #fm .s_ipt_wr:hover {
    box-shadow: 0 2px 5px #0000003b
  }
  .s_btn {
    color: #fff;
    transition: .3s;
    border-radius: 0 5px 5px 0;
    font-size: 10px;
    background: #5892e3;
    border: none;
    box-shadow: 0 2px 5px #0000001f
  }
  .s_btn:hover {
    border: none;
    background: #4879bd !important;
    box-shadow: 0 2px 5px #0000003b
  }
  select {
    transition: .3s;
    background: #000;
    padding: 5px;
    border: none !important;
    box-shadow: 0 2px 5px #0000001f
  }
  #gxszButton .prefpanelgo, #xs-subscription .create-btn, .fb-btn-primary {
    color: #fff;
    transition: .3s;
    background: #5892e3;
    border: none;
    box-shadow: 0 2px 5px #0000001f
  }
  #content_left .result, #dtl_l .relative_content li, div#leftnav {
    padding: 20px 20px 15px;
    margin-top: 0;
    margin-bottom: 20px;
    border-radius: 0;
    background-color: #fff;
    box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -webkit-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    -moz-box-shadow: 0 0 20px 2px rgba(0, 0, 0, .1);
    transition: all .25s cubic-bezier(.23, 1, .32, 1) 0s
  }
  #content_left .result:hover, #dtl_l .relative_content li:hover {
    box-shadow: 0 0 2px gray !important;
    background: #f3f3f3 !important
  }
  #navcard_wrapper .navlist_content .navitem {
    padding: 10px
  }
  #head_wr.gj #head.gj {
    background-color: #65808e;
    box-shadow: 0 2px 10px rgba(0, 0, 0, .3)
  }
  #left_menu_content.gj .left_menu_logo {
    background: #65808e;
    box-shadow: 0 2px 10px rgba(0, 0, 0, .3)
  }
  #head_wr.gj #head.gj .s_logo .s_logo_img {
    transition: .3s;
    opacity: .7
  }
  #head_wr.gj #head.gj .s_logo .s_logo_img:hover {
    opacity: 1
  }
  .s_ipt {
    transition: .3s;
    background: #fff0;
    color: #000000d1
  }
  #head.gj .s_ipt_wr.gj {
    transition: .3s;
    background: #ffffff80;
    border-radius: 5px
  }
  #head.gj .s_ipt_wr.gj:hover {
    transition: .3s;
    background: #ffffffc4
  }
  #head.gj .s_ipt_wr.gj:hover .s_ipt {
    color: #999
  }
  #left_menu_content.gj .main_menu_wr .menu_listitem {
    transition: all .1s .3s;
    height: 40px
  }
  #left_menu_content.gj .main_menu_wr .menu_listitem a {
    line-height: 40px;
    height: 40px;
    transition: all .1s
  }
  #leftnav {
    position: relative;
    left: -20px;
    margin: 0 10px 0 0
  }
  .batch-container .batch-btn-wr {
    position: fixed;
    right: 28px;
    bottom: 28px
  }
  .batch-container .batch-btn-wr .batch-btn span {
    background: #00000029
  }
  #dtl_l .rel_title {
    margin-top: 0
  }
  #leftnav .s_btn {
    top: 2px;
    left: auto;
    right: -25px;
    position: absolute;
    outline: 0
  }
}
